import React from 'react';


const SendMessageForm = ({message,setMessage,handleSubmit}) => {
    
    
    return (
        <div>
            <form className="new-name my-3">
                <div className="input-group">
                    <input type="text" id="name"  className="form-control" value={name} onChange={(e)=>{setName(e.target.value)}} placeholder="Get a name" required />
                    <input type="text" id="message"  className="form-control" value={message} onChange={(e)=>{setMessage(e.target.value)}} onKeyPress={(e)=>{e.key === 'Enter' ? handleSubmit(e) :null}} placeholder="Type your message..."  required/>
                    <button type="submit" onClick={(e) => handleSubmit(e)} className="btn btn-success btn-lg btn-sm">Send</button>
                </div>
                
            </form>
            
        </div>
    )
}

export default SendMessageForm
