import React, { useState } from 'react';
import { Link } from "react-router-dom";

const Join = () => {
    return (
        <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join</h1>
                <form className="new-name my-3">
                    <div className="input-group">
                        <input placeholder="Get A Name.." className="joinInput" type="text" className="form-control" required />
                        <input placeholder="Join A Room" className="joinInput" className="form-control" type="text"  required/>
                        <Link to={`/join`}>
                            <button className="btn btn-success btn-lg btn-sm" type="submit">Start Chat</button>
                        </Link>
                    </div>
                
                </form>
            </div>
        </div>
    );
}

export default Join
