import React, { useState } from 'react';
import { Link } from "react-router-dom";

const Join = () => {
    return (
        
            <>
                <form className="form-signin">
                    <h1 className="h3 mb-3 font-weight-normal">Please Register</h1>
                    <label htmlFor="inputEmail" className="sr-only">Get a name</label>
                    <input type="text" id="inputEmail" className="form-control" placeholder="Name.." required autofocus/>
                    <label htmlFor="inputPassword" className="sr-only">Join a room</label>
                    <input type="text" id="inputPassword" className="form-control" placeholder="Room.." required/>

                    <Link to={`/join`}>
                        <button className="btn btn-lg btn-primary btn-block" type="submit">Start Chat</button>
                    </Link>
                </form>
            </>
        
    );
}

export default Join
