import React,{useState} from 'react';
import io from 'socket.io-client';

const SendMessageForm = () => {
    const [name,setName] = useState('');
    const [message,setMessage]=useState('');
    const socket = io('http://localhost');
    socket.on('chat',(message)=>{
        console.log(message)
    });
    const handleSubmit =(e)=>{
        e.preventDefault();
        console.log(name, message);
        socket.emit('chatMessage',message);
        setName('');
        setMessage('');
    }
    return (
        <div>
            <form className="new-name my-3" onSubmit={handleSubmit}>
                <div className="input-group">
                    <input type="text" id="name"  className="form-control" value={name} onChange={(e)=>{setName(e.target.value)}} placeholder="Get a name" required />
                    <input type="text" id="message"  className="form-control" value={message} onChange={(e)=>{setMessage(e.target.value)}} placeholder="Type your message..."  required/>
                    <button type="submit" className="btn btn-success btn-lg btn-sm">Send</button>
                </div>
                
            </form>
            
        </div>
    )
}

export default SendMessageForm
